import React from 'react';

class App extends React.Component{

	constructor(props){
		super(props);
		this.state ={
			items: [],
			isLoaded: false,
		}
	}

	componentDidMount(){

		fetch('http://opentable.herokuapp.com/api/restaurants?country=CA')
		.then(res => res.json())
		.then(json => {
			this.setState({
				isLoaded: true,
				items: json,
			})
		});
	}	
	render(){

		var {isLoaded, items} = this.state;

		return(
			<div className="App">

			</div>
		);
	}
}

export default App;
